import { Link } from "react-router-dom";
import { FileText } from "lucide-react";
import { useState, ChangeEvent } from "react"; // Added useState and ChangeEvent

// Define interfaces for the API response
interface ApiResponse {
  preview: any[]; // You might want to define a more specific type for preview rows
  columns: string[];
}

export default function PredictionPage() {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [apiResponse, setApiResponse] = useState<ApiResponse | null>(null); // This will be populated by a separate prediction call
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [uploadSuccessMessage, setUploadSuccessMessage] = useState<string | null>(null); // Added for upload status

  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedFile(event.target.files[0]);
      setApiResponse(null); // Reset previous response
      setError(null); // Reset previous error
      setUploadSuccessMessage(null); // Reset success message
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) {
      setError("Please select a file first.");
      return;
    }

    setIsLoading(true);
    setError(null);
    setApiResponse(null); // Clear previous prediction results
    setUploadSuccessMessage(null); // Clear previous success message

    const formData = new FormData();
    formData.append("file", selectedFile);

    // Retrieve the token from where you stored it (e.g., localStorage)
    const authToken = localStorage.getItem("authToken"); 

    if (!authToken) {
      setError("Authentication token not found. Please log in.");
      setIsLoading(false);
      return;
    }

    try {
      const response = await fetch("http://127.0.0.1:8000/api/upload/", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
        body: formData,
      });

      if (!response.ok) {
        // Try to parse error message from backend, otherwise use status text
        let errorMessage = `HTTP error! status: ${response.status}`;
        try {
            const errorData = await response.json();
            errorMessage = errorData.message || errorData.detail || errorMessage;
        } catch (e) {
            // If response is not JSON or error in parsing, use status text
            errorMessage = response.statusText || errorMessage;
        }
        throw new Error(errorMessage);
      }

      // Assuming the upload API returns a simple success message or status
      // For example, it might return { message: "File uploaded successfully" }
      // Or you might not even need to parse a body if a 200/201 status is enough.
      // const uploadResult = await response.json(); 
      // console.log("Upload successful:", uploadResult); 
      setUploadSuccessMessage("File uploaded successfully! Ready for prediction.");
      // The `apiResponse` (preview and columns) will be set by a separate prediction call later.

    } catch (err: any) {
      setError(err.message || "Failed to upload file.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <h1 className="text-3xl font-bold mb-8">Churn Prediction</h1>
          
          <div className="bg-white rounded-lg shadow-md p-6 mb-8">
            <h2 className="text-xl font-semibold mb-4">1. Upload Data</h2>
            <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
              <FileText className="mx-auto h-12 w-12 text-gray-400" />
              <div className="mt-4">
                <label htmlFor="file-upload" className="cursor-pointer bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700">
                  Choose File
                </label>
                <input id="file-upload" type="file" className="hidden" accept=".csv" onChange={handleFileChange} />
                <p className="mt-2 text-sm text-gray-500">or drag and drop (drag and drop not implemented yet)</p>
                {selectedFile && (
                  <p className="mt-2 text-sm text-green-600">Selected file: {selectedFile.name}</p>
                )}
              </div>
              {selectedFile && (
                <button
                  onClick={handleFileUpload}
                  disabled={isLoading}
                  className="mt-4 bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 disabled:bg-gray-400"
                >
                  {isLoading ? "Uploading..." : "Upload File"}
                </button>
              )}
            </div>
            {error && (
              <p className="mt-4 text-sm text-red-600 text-center">{error}</p>
            )}
            {uploadSuccessMessage && (
              <p className="mt-4 text-sm text-green-600 text-center">{uploadSuccessMessage}</p>
            )}
          </div>

          {/* Section for triggering prediction and displaying results will be separate */}
          {/* For now, the existing apiResponse display logic remains, 
              it will be populated when you implement the prediction call */}
          {apiResponse && (
            <div className="bg-white rounded-lg shadow-md p-6 mb-8">
              <h2 className="text-xl font-semibold mb-2">Processed Data Preview</h2>
              <p className="text-sm text-gray-600 mb-4">First 5 rows of the processed data:</p>
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      {apiResponse.columns.map((colName, index) => (
                        <th key={index} scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          {colName}
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {apiResponse.preview.map((row, rowIndex) => (
                      <tr key={rowIndex}>
                        {apiResponse.columns.map((colName, colIndex) => (
                          <td key={colIndex} className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            {String(row[colName])} {/* Ensure value is stringified */}
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <h3 className="text-lg font-semibold mt-6 mb-2">Column Names</h3>
              <ul className="list-disc list-inside pl-5 text-sm text-gray-700">
                {apiResponse.columns.map((colName, index) => (
                  <li key={index}>{colName}</li>
                ))}
              </ul>
            </div>
          )}

          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">Prediction Results</h2>
            <div className="space-y-4">
              <div className="flex justify-between items-center p-4 bg-gray-50 rounded">
                <span className="font-medium">Total Customers</span>
                <span className="text-gray-600">0</span>
              </div>
              <div className="flex justify-between items-center p-4 bg-gray-50 rounded">
                <span className="font-medium">Churn Risk</span>
                <span className="text-gray-600">0%</span>
              </div>
              <div className="flex justify-between items-center p-4 bg-gray-50 rounded">
                <span className="font-medium">High Risk Customers</span>
                <span className="text-gray-600">0</span>
              </div>
            </div>
          </div>

          <div className="mt-8 text-center">
            <Link to="/" className="text-blue-600 hover:text-blue-800">
              Back to Home
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}
