import React, { useState } from 'react';
import { getAIInsights, askAI } from '../utils/openai';

interface AIInsightsProps {
  predictionData: any;
}

const AIInsights: React.FC<AIInsightsProps> = ({ predictionData }) => {
  const [insights, setInsights] = useState<{ summary: string; recommendations: string[] } | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState<string | null>(null);
  const [askingQuestion, setAskingQuestion] = useState(false);

  const handleGetInsights = async () => {
    setLoading(true);
    setError(null);
    try {
      const result = await getAIInsights(predictionData);
      setInsights(result);
    } catch (err) {
      setError('Failed to get AI insights. Please try again.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleAskQuestion = async () => {
    if (!question.trim()) return;
    
    setAskingQuestion(true);
    setError(null);
    try {
      const result = await askAI(question, predictionData);
      setAnswer(result);
    } catch (err) {
      setError('Failed to get AI response. Please try again.');
      console.error(err);
    } finally {
      setAskingQuestion(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mt-6">
      <h2 className="text-2xl font-semibold mb-4">AI Insights</h2>
      
      {!insights && !loading && (
        <button
          onClick={handleGetInsights}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors"
        >
          Get AI Insights
        </button>
      )}

      {loading && (
        <div className="flex items-center justify-center py-4">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      )}

      {error && (
        <div className="text-red-600 mt-2">
          {error}
        </div>
      )}

      {insights && (
        <div className="space-y-4">
          <div>
            <h3 className="text-lg font-medium mb-2">Summary</h3>
            <p className="text-gray-700">{insights.summary}</p>
          </div>

          <div>
            <h3 className="text-lg font-medium mb-2">Recommendations</h3>
            <ul className="list-disc list-inside space-y-2">
              {insights.recommendations.map((rec, index) => (
                <li key={index} className="text-gray-700">{rec}</li>
              ))}
            </ul>
          </div>
        </div>
      )}

      <div className="mt-6 border-t pt-6">
        <h3 className="text-lg font-medium mb-4">Ask AI a Question</h3>
        <div className="flex gap-2">
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Ask about the prediction results..."
            className="flex-1 px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleAskQuestion}
            disabled={askingQuestion || !question.trim()}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition-colors disabled:opacity-50"
          >
            {askingQuestion ? 'Asking...' : 'Ask'}
          </button>
        </div>

        {answer && (
          <div className="mt-4 p-4 bg-gray-50 rounded">
            <p className="text-gray-700">{answer}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AIInsights;
